// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

const all = $items("Store All Outputs")[0].json.all_outputs;
const current = $json;
const others = all.filter(i => i.framework !== current.framework);
return [{
  json: {
    own_framework: current.framework,
    own_output: current.output,
    others_outputs: others
  }
}];
