const nodes = ["Telegram Trigger", "Design Thinking","Lean Startup","SCAMPER", "TRIZ","Jobs to Be Done","Ten Types of Innovation (Doblin)","Merge", "AI Agent","Telegram"];
const workflowDataArray = [];

// Recursively resolve expressions in any structure
function resolveAllExpressions(value, contextJson) {
  if (typeof value === "string" && value.startsWith("={{") && value.endsWith("}}")) {
    try {
      const inner = value.slice(3, -2).trim();
      const $json = contextJson;
      return eval(inner);
    } catch (e) {
      return `Error: ${e.message}`;
    }
  } else if (Array.isArray(value)) {
    return value.map(item => resolveAllExpressions(item, contextJson));
  } else if (value !== null && typeof value === "object") {
    return Object.fromEntries(
      Object.entries(value).map(([k, v]) => [k, resolveAllExpressions(v, contextJson)])
    );
  }
  return value;
}

for (let i = 0; i < nodes.length; i++) {
  const node = nodes[i];
  let output = {};
  let previousSteps = {};

  try {
    output = $(node).all().map(item => item.json)[0];
    previousSteps = output['intermediateSteps'];
    output['intermediateSteps'].destroy;
  } catch (_) {}

  let input = {};
  try {
    const parameters = JSON.parse(JSON.stringify($(node).params)) || {};
    const prevNode = i > 0 ? nodes[i - 1] : null;
    let contextJson = {};
    try {
      contextJson = prevNode ? ($(prevNode).first()?.json || {}) : {};
    } catch {}
    input = resolveAllExpressions(parameters, contextJson);

    workflowDataArray.push({
      slug: node,
      input: {
        ...input,
        previousSteps
      },
      output,
    });
  } catch (_) {}
}

return {
  workflowData: workflowDataArray,
  agentName: "Agente Rag v0"
};
