const xlsx = require('xlsx');

// Leer el buffer desde el campo binario correctamente
const binaryData = items[0].binary.data.data;
const buffer = Buffer.from(binaryData, 'base64');

// Leer el archivo Excel completo
const workbook = xlsx.read(buffer, { type: 'buffer' });

const result = [];

// Iterar sobre todas las hojas
for (const sheetName of workbook.SheetNames) {
  const sheet = workbook.Sheets[sheetName];
  const jsonData = xlsx.utils.sheet_to_json(sheet, { defval: null });

  // Para cada fila, agregarla como un item individual incluyendo el nombre de la hoja
  for (const row of jsonData) {
    result.push({
      json: {
        sheetName,
        ...row, // A√±ade las columnas del Excel como propiedades
      }
    });
  }
}

return result;
