$json.text = $input.first().json.output
  .replace(/\\\\/g, "\\")  // Escapar backslashes dobles
  .replace(/\\r/g, '\r')   // Reemplazar '\r' por su carácter real
  .replace(/\\\\/g, '\\')  // Escapar dobles backslashes nuevamente
  .replace(/\\"/g, '"')    // Reemplazar comillas escapadas
  .replace(/[\u200B-\u200D\uFEFF]/g, "") // Elimina espacios invisibles (Zero Width)
  .replace(/\n/g, "  ") // Elimina TODOS los saltos de línea
  .replace(/"/g, "'") // Elimina todas las comillas dobles
  .replace(/\*/g, "") // Elimina todos los asteriscos
  .replace(/\esa/g, "la");


return [{
  json: $json
}];

const crypto = require('crypto');

const appKey = "1750733317000521";
const secretKey = "532995f1c23cb578cb20527a198d6ed6";
const userId = 'uid';
const coreType = 'speak.eval.pro';
const questionPrompt = "Please talk about any topic you like.";
const audioType = 'ogg';
const sampleRate = 16000;

// Firmas
const timestamp = Date.now().toString();
const sigConnect = crypto.createHash('sha1').update(appKey + timestamp + secretKey).digest('hex');
const sigStart = crypto.createHash('sha1').update(appKey + timestamp + userId + secretKey).digest('hex');

// UUID
const uuid = 'xxxxxxxxxxxx4xxxyxxxxxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
  const r = Math.random() * 16 | 0;
  const v = c === 'x' ? r : (r & 0x3 | 0x8);
  return v.toString(16);
}).toUpperCase();

const finalPayload = {
  connect: {
    cmd: "connect",
    param: {
      sdk: {
        version: 16777472,
        source: 9,
        protocol: 2
      },
      app: {
        applicationId: appKey,
        sig: sigConnect,
        timestamp: timestamp
      }
    }
  },
  start: {
    cmd: "start",
    param: {
      app: {
        applicationId: appKey,
        userId: userId,
        timestamp: timestamp,
        sig: sigStart
      },
      audio: {
        sampleBytes: 2,
        sampleRate,
        audioType,
        channel: 1
      },
      request: {
        coreType,
        test_type: "ielts",
        task_type: "ielts_part1",
        question_prompt: questionPrompt,
        phoneme_output: 1,
        model: "non_native",
        penalize_offtopic: 1,
        decimal_point: 0,
        tokenId: uuid
      }
    }
  }
};

return [
  {
    json: {
      text: JSON.stringify(finalPayload)
    },
    binary: items[0].binary  // El archivo .mp3
  }
];

// Acceder a la respuesta JSON del nodo anterior
const response = $input.all()[0].json;
const result = response.result;
const params = response.params;

// 1. Extraer correcciones gramaticales de forma clara
const grammar_corrections = result.sentences
  .filter(s => s.grammar && s.grammar.corrected && s.sentence !== s.grammar.corrected)
  .map(s => ({
    original_sentence: s.sentence,
    corrected_sentence: s.grammar.corrected,
  }));

// 2. Extraer palabras con errores de pronunciación significativos
// Un score por debajo de 60 se considera 'pobre' o 'regular' y necesita atención.
const pronunciation_issues = result.sentences.flatMap(sentence => 
  sentence.details
    .filter(word => word.pronunciation < 60)
    .map(word => ({
      word: word.word,
      pronunciation_score: word.pronunciation,
      phoneme_scores: word.phonics.map(p => ({
        spell: p.spell,
        phoneme: p.phoneme.join(', '),
        score: p.overall
      }))
    }))
);

// 3. Crear el objeto final con toda la información importante para el agente
const evaluation_data = {
  // --- Contexto General y Puntuación Global ---
  context: {
    question_prompt: params.request.question_prompt,
    task_type: params.request.task_type
  },
  overall_evaluation: {
    overall_score: result.overall,
    cefr_level_overall: result.equivalent_scores.cefr_levels.overall,
    transcription: result.transcription,
  },

  // --- Evaluación de Pronunciación ---
  pronunciation_feedback: {
    pronunciation_score: result.pronunciation,
    pronunciation_cefr_level: result.equivalent_scores.cefr_levels.pronunciation,
    pronunciation_stats: result.pronunciation_stats, // { poor_word_pct, good_word_pct, fair_word_pct }
    words_with_pronunciation_issues: pronunciation_issues,
  },

  // --- Evaluación de Fluidez ---
  fluency_feedback: {
    fluency_score: result.fluency_coherence,
    fluency_cefr_level: result.equivalent_scores.cefr_levels.fluency,
    speech_speed_wpm: result.speed, // Palabras por minuto
    rhythm_score: result.rhythm,
    fluency_stats: result.fluency_stats, // { pause_cnt, liaison_cnt }
  },

  // --- Evaluación de Gramática ---
  grammar_feedback: {
    grammar_score: result.grammar,
    grammar_cefr_level: result.equivalent_scores.cefr_levels.grammar,
    grammar_stats: result.grammar_stats, // { accurate_sent_pct, grammar_error_cnt }
    grammar_corrections: grammar_corrections,
  },

  // --- Evaluación de Vocabulario (Recurso Léxico) ---
  vocabulary_feedback: {
    vocabulary_score: result.lexical_resource,
    vocabulary_cefr_level: result.equivalent_scores.cefr_levels.vocab,
    vocabulary_stats: {
      total_words: result.vocabulary_stats.word_cnt,
      unique_words: result.vocabulary_stats.unique_word_cnt,
      academic_words_used: result.vocabulary_stats.academic_words,
      cefr_level_distribution: {
        A1_percent: result.vocabulary_stats.CEFR_A1_pct,
        A2_percent: result.vocabulary_stats.CEFR_A2_pct,
        B1_percent: result.vocabulary_stats.CEFR_B1_pct,
        B2_percent: result.vocabulary_stats.CEFR_B2_pct,
        C1_percent: result.vocabulary_stats.CEFR_C1_pct,
        C2_percent: result.vocabulary_stats.CEFR_C2_pct,
      }
    }
  }
};

// Devolver el objeto estructurado para el agente evaluador
return {
  json: {"evaluation": evaluation_data}
};

