try {
  // Acceder a la respuesta JSON del nodo anterior
  const response = $input.all()[0].json;
  const result = response.result;
  const params = response.params;

  if (!result || !params || !result.sentences || !result.equivalent_scores || !result.equivalent_scores.cefr_levels) {
    throw new Error("Datos incompletos en la respuesta");
  }

  // 1. Extraer correcciones gramaticales
  const grammar_corrections = result.sentences
    .filter(s => s.grammar && s.grammar.corrected && s.sentence !== s.grammar.corrected)
    .map(s => ({
      original_sentence: s.sentence,
      corrected_sentence: s.grammar.corrected,
    }));

  // 2. Extraer palabras con errores de pronunciación significativos
  const pronunciation_issues = result.sentences.flatMap(sentence =>
    (sentence.details || [])
      .filter(word => word.pronunciation < 60)
      .map(word => ({
        word: word.word,
        pronunciation_score: word.pronunciation,
        phoneme_scores: (word.phonics || []).map(p => ({
          spell: p.spell,
          phoneme: p.phoneme?.join(', ') || '',
          score: p.overall
        }))
      }))
  );

  // 3. Crear el objeto final con toda la información importante
  const evaluation_data = {
    context: {
      question_prompt: params.request?.question_prompt || null,
      task_type: params.request?.task_type || null
    },
    overall_evaluation: {
      overall_score: result.overall ?? null,
      cefr_level_overall: result.equivalent_scores.cefr_levels.overall ?? null,
      transcription: result.transcription ?? null,
    },
    pronunciation_feedback: {
      pronunciation_score: result.pronunciation ?? null,
      pronunciation_cefr_level: result.equivalent_scores.cefr_levels.pronunciation ?? null,
      pronunciation_stats: result.pronunciation_stats ?? {},
      words_with_pronunciation_issues: pronunciation_issues,
    },
    fluency_feedback: {
      fluency_score: result.fluency_coherence ?? null,
      fluency_cefr_level: result.equivalent_scores.cefr_levels.fluency ?? null,
      speech_speed_wpm: result.speed ?? null,
      rhythm_score: result.rhythm ?? null,
      fluency_stats: result.fluency_stats ?? {},
    },
    grammar_feedback: {
      grammar_score: result.grammar ?? null,
      grammar_cefr_level: result.equivalent_scores.cefr_levels.grammar ?? null,
      grammar_stats: result.grammar_stats ?? {},
      grammar_corrections: grammar_corrections,
    },
    vocabulary_feedback: {
      vocabulary_score: result.lexical_resource ?? null,
      vocabulary_cefr_level: result.equivalent_scores.cefr_levels.vocab ?? null,
      vocabulary_stats: {
        total_words: result.vocabulary_stats?.word_cnt ?? 0,
        unique_words: result.vocabulary_stats?.unique_word_cnt ?? 0,
        academic_words_used: result.vocabulary_stats?.academic_words ?? 0,
        cefr_level_distribution: {
          A1_percent: result.vocabulary_stats?.CEFR_A1_pct ?? 0,
          A2_percent: result.vocabulary_stats?.CEFR_A2_pct ?? 0,
          B1_percent: result.vocabulary_stats?.CEFR_B1_pct ?? 0,
          B2_percent: result.vocabulary_stats?.CEFR_B2_pct ?? 0,
          C1_percent: result.vocabulary_stats?.CEFR_C1_pct ?? 0,
          C2_percent: result.vocabulary_stats?.CEFR_C2_pct ?? 0,
        }
      }
    }
  };

  // Devolver datos si todo está bien
  return {
    json: { evaluation: evaluation_data }
  };

} catch (error) {
  // Si algo falla, devolver error personalizado
  return {
    json: {
      error: true,
      message: $input.first().json.error || "Ocurrió un error inesperado al procesar la evaluación."
    }
  };
}

