const rawOutput = $input.first().json.output;

let text = rawOutput;

// Paso 1: Intentar parsear como JSON completo o como objeto parcial
try {
  if (typeof rawOutput === 'string') {
    let text = rawOutput.trim();

    // Solo quitar comillas dobles si el string entero est√° envuelto entre ellas
    if (text.startsWith('"') && text.endsWith('"')) {
      text = text.slice(1, -1); // eliminar comillas externas
    }

    // Reemplazar caracteres escapados
    text = text
      .replace(/\\"/g, '"')   // desescapar comillas internas
      .replace(/\\n/g, '\n')  // desescapar saltos de l√≠nea
      .trim();

    // Asegurar formato de objeto JSON
    if (!text.startsWith('{')) {
      text = `{${text}}`;
    }
  }

  const data = JSON.parse(text);

  // Formatear resultado limpio
  return [{
    json: {
      titulo: data.titulo || null,
      contenido: data.contenido || null,
      slug: data.slug || null,
      metaDescripcion: data.metaDescripcion || null,
      tags: Array.isArray(data.tags) ? data.tags : [],
      fechaPublicacion: data.fechaPublicacion || null,
      modo_creacion: data.modo_creacion || null
    }
  }];
} catch (err) {
  // Paso 2: Si no se puede parsear, usar regex como fallback
  const fallback = {
    titulo: null,
    contenido: null,
    slug: null,
    metaDescripcion: null,
    tags: [],
    fechaPublicacion: null,
    modo_creacion: null
  };

  const patterns = {
    titulo: /"titulo":\s*"([^"]+)"/,
    contenido: /"contenido":\s*"([\s\S]+?)",\s*"slug"/,
    slug: /"slug":\s*"([^"]+)"/,
    metaDescripcion: /"metaDescripcion":\s*"([^"]+)"/,
    tags: /"tags":\s*(\[[^\]]+\])/,
    fechaPublicacion: /"fechaPublicacion":\s*"([^"]+)"/,
    modo_creacion: /"modo_creacion":\s*"([^"]+)"/
  };

  for (const [key, regex] of Object.entries(patterns)) {
    const match = text.match(regex);
    if (match && match[1]) {
      if (key === 'tags') {
        try {
          fallback[key] = JSON.parse(match[1]);
        } catch {
          fallback[key] = match[1]
            .replace(/[\[\]"]/g, '')
            .split(',')
            .map(s => s.trim());
        }
      } else {
        fallback[key] = match[1];
      }
    }
  }

  return [{ json: fallback }];
}

