// Funci√≥n para calcular coincidencias
function calcularCoincidencias(imagen) {
    let puntuacion = 0;
    const textoBusqueda = `${titulo} ${descripcion} ${tags.join(' ')} ${categoria}`.toLowerCase();
    const camposImagen = `${imagen.title || ''} ${imagen.description || ''}`.toLowerCase();

    // Dividir en palabras clave
    const palabrasBusqueda = textoBusqueda.split(/\s+/);
    const palabrasImagen = camposImagen.split(/\s+/);

    // Calcular coincidencias exactas
    palabrasBusqueda.forEach(palabra => {
        if (palabrasImagen.includes(palabra)) {
            puntuacion += 2; // Peso mayor para coincidencias exactas
        } else if (camposImagen.includes(palabra)) {
            puntuacion += 1; // Peso menor para coincidencias parciales
        }
    });

    // Bonus por coincidencia en t√≠tulo
    if (imagen.title && titulo.toLowerCase().includes(imagen.title.toLowerCase())) {
        puntuacion += 3;
    }

    return puntuacion;
}

