  encargado = $('Get suplier2').first().json["ENCARGADO "].split(' ')[0];
}

// Construir mensajes para cada finca
const mensajes = [];

for (const finca in agrupados) {
  let mensaje = `${saludo} - *${encargado}*\nSe envÃ­a resumen de pedidos solicitados para el ${fechaEntrega}.\nPor favor responder sobre este mensaje las novedades y confirmar el status del pedido.\n\n`;

  for (const numPedido in agrupados[finca]) {
