  }

  mensaje = mensaje.trim();

  mensajes.push({
    json: {
      FINCA: finca,
      MENSAJE_RESUMEN: mensaje,
      FECHA: fechaEntrega,
      ENTREGA: entregaTexto,
      TELEFONO: $('enviar confirmacion Pedido1').first().json['NÂº DE TELÃ‰FONO']
    }
  });
}

return mensajes;

const data = items[0].json.data;

const uniqueCombos = new Set();
const result = [];

for (const entry of data) {
  const key = `${entry.PRODUCTO} | ${entry.PALET} | ${entry.CAJAS}`;

  if (!uniqueCombos.has(key)) {
    uniqueCombos.add(key);
    result.push({
      PRODUCTO: entry.PRODUCTOS,
      PALET: entry.PALET,
      CAJAS: entry.CAJAS
    });
  }
}

return [{
  json: {
    data: result
  }
}];

// Obtener el texto plano del agente
const raw = items[0].json.output;

// Intentar extraer el JSON entre los delimitadores ```json y ```
const match = raw.match(/```json\s*([\s\S]*?)\s*```/);

// Si no encuentra un bloque vÃ¡lido, intentar parsear como JSON directo
const cleanJsonString = match ? match[1] : raw;

// Intentar parsear el contenido
let parsed;
try {
  parsed = JSON.parse(cleanJsonString);
} catch (error) {
  throw new Error("No se pudo parsear el JSON: " + error.message);
}

// Verificamos que sea un array
if (!Array.isArray(parsed)) {
  throw new Error("El JSON parseado no es un array.");
}

// Devolvemos cada objeto como item independiente
return parsed.map(obj => ({ json: obj }));

