const body = items[0].json.body;

const prev = body.previousOrder.products;
const updated = body.updatedOrder.products;

// Función para formatear fecha
function formatearFecha(fechaISO) {
  const fecha = new Date(fechaISO);
  const dia = String(fecha.getDate()).padStart(2, '0');
  const mes = String(fecha.getMonth() + 1).padStart(2, '0');
  const año = fecha.getFullYear();
  const horas = String(fecha.getHours()).padStart(2, '0');
  const minutos = String(fecha.getMinutes()).padStart(2, '0');
  const segundos = String(fecha.getSeconds()).padStart(2, '0');
  return `${dia}/${mes}/${año}, ${horas}:${minutos}:${segundos}`;
}

const fechaFormateada = formatearFecha(body.updatedOrder.createdAt);
let resultados = [];

// Recorremos productos actualizados
for (let i = 0; i < updated.length; i++) {
  const productoNuevo = updated[i];
  const productoPrevio = prev.find(p =>
    p.product.name === productoNuevo.product.name &&
    p.palet.name === productoNuevo.palet.name
  );

  if (!productoPrevio) {
    const mismoProductoOtroPalet = prev.find(p =>
      p.product.name === productoNuevo.product.name &&
      p.palet.name !== productoNuevo.palet.name
    );

    if (mismoProductoOtroPalet) {
      resultados.push({
        tipo: 'Delete',
        NUM_PEDIDO: body.updatedOrder.receptionNumber,
        CIF_CLIENTE: body.updatedOrder.clientCIF,
        NOMBRE_CLIENTE: body.updatedOrder.clientName,
        PRODUCTOS: mismoProductoOtroPalet.product.name,
        PALET: mismoProductoOtroPalet.palet.name,
        CANT_PALETS: mismoProductoOtroPalet.paletQuantity,
        CAJAS: mismoProductoOtroPalet.caja.name,
        CANT_CAJAS: mismoProductoOtroPalet.cajaQuantity,
        STATUS: body.updatedOrder.status,
        FECHA: fechaFormateada
      });

      resultados.push({
        tipo: 'New',
        NUM_PEDIDO: body.updatedOrder.receptionNumber,
        CIF_CLIENTE: body.updatedOrder.clientCIF,
        NOMBRE_CLIENTE: body.updatedOrder.clientName,
        PRODUCTOS: productoNuevo.product.name,
        PALET: productoNuevo.palet.name,
        CANT_PALETS: productoNuevo.paletQuantity,
        CAJAS: productoNuevo.caja.name,
        CANT_CAJAS: productoNuevo.cajaQuantity,
        STATUS: body.updatedOrder.status,
        FECHA: fechaFormateada
      });
    } else {
      resultados.push({
        tipo: 'New',
        NUM_PEDIDO: body.updatedOrder.receptionNumber,
        CIF_CLIENTE: body.updatedOrder.clientCIF,
        NOMBRE_CLIENTE: body.updatedOrder.clientName,
        PRODUCTOS: productoNuevo.product.name,
        PALET: productoNuevo.palet.name,
        CANT_PALETS: productoNuevo.paletQuantity,
        CAJAS: productoNuevo.caja.name,
        CANT_CAJAS: productoNuevo.cajaQuantity,
        STATUS: body.updatedOrder.status,
        FECHA: fechaFormateada
      });
    }
    continue;
  }

  const cambios = [];

  // Verificamos cambios y preparamos el entry solo si hay cambios reales
  if (productoNuevo.paletQuantity !== productoPrevio.paletQuantity) {
    cambios.push(`CANT_PALETS: ${productoPrevio.paletQuantity} → ${productoNuevo.paletQuantity}`);
  }

  if (productoNuevo.cajaQuantity !== productoPrevio.cajaQuantity) {
    cambios.push(`CANT_CAJAS: ${productoPrevio.cajaQuantity} → ${productoNuevo.cajaQuantity}`);
  }

  if (productoNuevo.caja.name !== productoPrevio.caja.name) {
    cambios.push(`CAJAS: ${productoPrevio.caja.name} → ${productoNuevo.caja.name}`);
  }

  if (cambios.length > 0) {
    resultados.push({
      tipo: 'Update',
      NUM_PEDIDO: body.updatedOrder.receptionNumber,
      CIF_CLIENTE: body.updatedOrder.clientCIF,
      NOMBRE_CLIENTE: body.updatedOrder.clientName,
      PRODUCTOS: productoNuevo.product.name,
      PALET: productoNuevo.palet.name,
      CANT_PALETS: productoNuevo.paletQuantity,
      CANT_PALETS_ANTES: productoPrevio.paletQuantity,
      CAJAS: productoNuevo.caja.name,
      CAJAS_ANTES: productoPrevio.caja.name,
      CANT_CAJAS: productoNuevo.cajaQuantity,
      CANT_CAJAS_ANTES: productoPrevio.cajaQuantity,
      CAMBIOS: cambios.join(' | '),
      STATUS: body.updatedOrder.status,
      FECHA: fechaFormateada
    });
  }
}

// Verificar eliminados
for (let i = 0; i < prev.length; i++) {
  const pPrev = prev[i];
  const existe = updated.find(p =>
    p.product.name === pPrev.product.name &&
    p.palet.name === pPrev.palet.name
  );
  if (!existe) {
    resultados.push({
      tipo: 'Delete',
      NUM_PEDIDO: body.updatedOrder.receptionNumber,
      CIF_CLIENTE: body.updatedOrder.clientCIF,
      NOMBRE_CLIENTE: body.updatedOrder.clientName,
      PRODUCTOS: pPrev.product.name,
      PALET: pPrev.palet.name,
      CANT_PALETS: pPrev.paletQuantity,
      CAJAS: pPrev.caja.name,
      CANT_CAJAS: pPrev.cajaQuantity,
      STATUS: body.updatedOrder.status,
      FECHA: fechaFormateada
    });
  }
}

// Eliminar duplicados exactos
function deduplicar(array) {
  const seen = new Set();
  return array.filter(item => {
    const clave = `${item.tipo}|${item.PRODUCTOS}|${item.PALET}|${item.CAJAS}|${item.CANT_CAJAS}|${item.CANT_PALETS}`;
    if (seen.has(clave)) {
      return false;
    }
    seen.add(clave);
    return true;
  });
}

const resultadosUnicos = deduplicar(resultados);

return resultadosUnicos.map(r => ({ json: r }));

const results = [];
const zonaHoraria = 'America/Bogota';
const ahora = new Date(new Date().toLocaleString('en-US', { timeZone: zonaHoraria }));

// Función para formatear fecha como "DD/MM/YYYY, HH:mm:ss"
function formatearFechaCompleta(fecha) {
  const dia = String(fecha.getDate()).padStart(2, '0');
  const mes = String(fecha.getMonth() + 1).padStart(2, '0');
  const año = fecha.getFullYear();
  const horas = String(fecha.getHours()).padStart(2, '0');
  const minutos = String(fecha.getMinutes()).padStart(2, '0');
  const segundos = String(fecha.getSeconds()).padStart(2, '0');
  return `${dia}/${mes}/${año}, ${horas}:${minutos}:${segundos}`;
}

// Función para solo la fecha
function formatearFecha(fecha) {
  const dia = String(fecha.getDate()).padStart(2, '0');
  const mes = String(fecha.getMonth() + 1).padStart(2, '0');
  const año = fecha.getFullYear();
  return `${dia}/${mes}/${año}`;
}

const fechaCompleta = formatearFechaCompleta(ahora);
const fechaSimple = formatearFecha(ahora);
const saludo = ahora.getHours() < 12 ? 'Buen día' : 'Buena tarde';
const entrega = `hoy - (${fechaSimple})`;

// Agrupar productos por proveedor
const proveedores = {};

for (const item of items) {
  const prod = item.json;
  const proveedorId = prod.PROVEEDOR_ID;

  if (!proveedores[proveedorId]) {
    proveedores[proveedorId] = {
      ...prod,
      PRODUCTOS: [],
      FECHA: fechaSimple,
      ENTREGA: entrega
    };
    delete proveedores[proveedorId].ID_PRODUCTO;
    delete proveedores[proveedorId].PRODUCTO;
    delete proveedores[proveedorId].CANT_PALETS;
    delete proveedores[proveedorId].CANT_CAJAS;
    delete proveedores[proveedorId].PALET;
    delete proveedores[proveedorId].CAJAS;
    delete proveedores[proveedorId].NUM_PEDIDO;
    delete proveedores[proveedorId].STATUS;
    delete proveedores[proveedorId].comentarios;
    delete proveedores[proveedorId].MENSAJE_PROVEEDOR;
    delete proveedores[proveedorId].FECHA_CANCELACION;
  }

  proveedores[proveedorId].PRODUCTOS.push({
    ...prod,
    STATUS: "Cancelado",
    FECHA: fechaCompleta,
    FECHA_CANCELACION: fechaCompleta
  });
}

// Crear mensaje para cada proveedor
for (const proveedor of Object.values(proveedores)) {
  const productosPorPedido = {};

  for (const prod of proveedor.PRODUCTOS) {
    const numPedido = prod.NUM_PEDIDO || 'SIN_PEDIDO';
    if (!productosPorPedido[numPedido]) {
      productosPorPedido[numPedido] = [];
    }
    productosPorPedido[numPedido].push(
      `- ${prod.CANT_PALETS} x ${prod.CANT_CAJAS} - ${prod.PRODUCTO} - ${prod.PALET} - ${prod.CAJAS}`
    );
  }

  let mensaje = `🚫 *Cancelación de pedido*\n${saludo}, le informamos que el siguiente pedido para ${entrega} ha sido cancelado:\n\n`;
  for (const [numPedido, lineas] of Object.entries(productosPorPedido)) {
    mensaje += `➡️ *pedido N°: ${numPedido}*\n`;
    mensaje += lineas.join('\n\n') + '\n\n';
  }

  proveedor.MENSAJE_PROVEEDOR = mensaje.trim();
  results.push({ json: proveedor });
}

return results;

// 1. Extraer y unir todos los productos válidos (CANT_PALETS > 0)
let productosValidos = [];

for (const item of items) {
  const proveedor = item.json;
  const productos = proveedor.PRODUCTOS || [];
  const status = "Cancelado";

  const filtrados = productos
    .filter(p => p.CANT_PALETS > 0)
    .map(p => ({
      row_number: p.row_number,
      ID_PRODUCTO: p.ID_PRODUCTO,
      PROVEEDOR_ID: proveedor.PROVEEDOR_ID,
      FINCA: proveedor.FINCA,
      N_SOLICITUD: p.N_SOLICITUD,
      PRODUCTO: p.PRODUCTO,
      STATUS: status,
    }));

  productosValidos.push(...filtrados);
}

// 2. Eliminar duplicados por combinación ID_PRODUCTO + N_SOLICITUD + numero de fila
const vistos = new Set();
const productosUnicos = productosValidos.filter(p => {
  const clave = `${p.N_SOLICITUD}-${p.row_number}`;
  if (vistos.has(clave)) return false;
  vistos.add(clave);
  return true;
});

// 3. Devolver los productos únicos
return productosUnicos.map(p => ({ json: p }));

const results = [];
const status = "Cancelado";

for (const item of items) {
  const productos = item.json.PRODUCTOS;
  const finca = item.json.FINCA;
  const proveedorId = item.json.PROVEEDOR_ID;
  const fecha = item.json.FECHA;
  const entrega = item.json.ENTREGA;

  for (const prod of productos) {
    if (prod.CANT_PALETS > 0) {
      results.push({
        json: {
          NUM_PEDIDO: prod.NUM_PEDIDO,
          FECHA: fecha,
          PROVEEDOR_ID: proveedorId,
          FINCA: finca,
          ID_PRODUCTO: prod.ID_PRODUCTO,
          PRODUCTO: prod.PRODUCTO,
          CANT_PALETS: prod.CANT_PALETS,
          CANT_CAJAS: prod.CANT_CAJAS,
          PALET: prod.PALET,
          CAJAS: prod.CAJAS,
          ENTREGA: entrega,
          STATUS: status,
        }
      });
    }
  }
}

// Eliminar duplicados por NUM_PEDIDO, PRODUCTO, PALET, CAJAS
const uniqueResultsMap = new Map();

for (const entry of results) {
  const j = entry.json;
  const key = `${j.NUM_PEDIDO}|${j.PRODUCTO}|${j.PALET}|${j.CAJAS}`;
  if (!uniqueResultsMap.has(key)) {
    uniqueResultsMap.set(key, entry);
  }
}

return Array.from(uniqueResultsMap.values());

// Obtener el texto plano del agente
const raw = items[0].json.output;

// Intentar extraer el JSON entre los delimitadores ```json y ```
const match = raw.match(/```json\s*([\s\S]*?)\s*```/);

// Si no encuentra un bloque válido, intentar parsear como JSON directo
const cleanJsonString = match ? match[1] : raw;

// Intentar parsear el contenido
let parsed;
try {
  parsed = JSON.parse(cleanJsonString);
} catch (error) {
  throw new Error("No se pudo parsear el JSON: " + error.message);
}

// Verificamos que sea un array
if (!Array.isArray(parsed)) {
  throw new Error("El JSON parseado no es un array.");
}

// Devolvemos cada objeto como item independiente
return parsed.map(obj => ({ json: obj }));

const proveedor = items[0].json;

const salida = [];

for (const cambio of proveedor.CAMBIOS) {
  // Verificar si hay "Cancelación" en TIPO_CAMBIO
  const esCancelacion = cambio.TIPO_CAMBIO.includes("Cancelación");

  salida.push({
    row_number: cambio.row_number,
    STATUS: esCancelacion ? "Cancelado" : cambio.STATUS,
    CANT_PALETS: cambio.DESPUES.CANT_PALETS,
    CANT_CAJAS: cambio.DESPUES.CANT_CAJAS,
    CAJAS: cambio.DESPUES.CAJAS,
    PALET: cambio.DESPUES.PALET,
    comentarios: cambio.comentarios
  });
}

return salida.map(item => ({ json: item }));

const resumen = {};

for (const item of items) {
  const data = item.json;
  const num_pedido = data.NUM_PEDIDO;

  for (const cambio of data.CAMBIOS) {
    const producto = cambio.PRODUCTO;
    const despues = cambio.DESPUES;
    const antes = cambio.ANTES;
    const proveedor = data.PROVEEDOR_ID;

    const palet = despues.PALET;
    const cajas = despues.CAJAS;
    const cant_palets = despues.CANT_PALETS;
    const cant_cajas = despues.CANT_CAJAS;

    const clave = `${num_pedido}||${producto}||${palet}||${cajas}`;

    if (!resumen[clave]) {
      resumen[clave] = {
        NUM_PEDIDO: num_pedido,
        PRODUCTO: producto,
        PALET: palet,
        CAJAS: cajas,
        CANT_PALETS: 0,
        CANT_CAJAS: 0,
        CAMBIOS_PROVEEDOR: [],
        COMENTARIOS: []
      };
    }

    resumen[clave].CANT_PALETS += cant_palets;
    resumen[clave].CANT_CAJAS = cant_cajas; // asumimos homogéneo

    // Comentario individual por proveedor
    const comentario = `Proveedor ${proveedor} - palets: ${antes.CANT_PALETS} → ${despues.CANT_PALETS}`;
    resumen[clave].COMENTARIOS.push(comentario);

    resumen[clave].CAMBIOS_PROVEEDOR.push({
      PROVEEDOR_ID: proveedor,
      ANTES: antes.CANT_PALETS,
      DESPUES: despues.CANT_PALETS
    });
  }
}

// Armar salida con trazabilidad
const salida = Object.values(resumen).map(r => {
  const comentario = 
    `Palets finales: ${r.CANT_PALETS}, cajas: ${r.CANT_CAJAS}, ` +
    `palet: ${r.PALET}, cajas: ${r.CAJAS}. Cambios por proveedor:\n- ` +
    r.COMENTARIOS.join('\n- ');

  return {
    json: {
      NUM_PEDIDO: r.NUM_PEDIDO,
      PRODUCTO: r.PRODUCTO,
      PALET: r.PALET,
      CAJAS: r.CAJAS,
      CANT_PALETS: r.CANT_PALETS,
      CANT_CAJAS: r.CANT_CAJAS,
      COMENTARIOS: comentario
    }
  };
});

return salida;
