const orderJsonKeys = (jsonObj) => {
  const ordered = {};
  Object.keys(jsonObj).sort().forEach(key => {
    ordered[key] = jsonObj[key];
  });
  return ordered;
}

// Check if file returned with content
if (Object.keys($input.all()[0].json).includes("content")) {
  // Decode base64 content and parse JSON
  const origWorkflow = JSON.parse(Buffer.from($input.all()[0].json.content, 'base64').toString());
  const n8nWorkflow = $input.all()[1].json;

  // Order JSON objects
  const orderedOriginal = orderJsonKeys(origWorkflow);
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Determine difference
  if (JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual)) {
    $input.all()[0].json.github_status = "same";
  } else {
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  }
  $input.all()[0].json.content_decoded = orderedOriginal;
// No file returned / new workflow
} else if (Object.keys($input.all()[0].json).includes("data")) {
  const origWorkflow = JSON.parse($input.all()[0].json.data);
  const n8nWorkflow = $input.all()[1].json;

  // Order JSON objects
  const orderedOriginal = orderJsonKeys(origWorkflow);
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Determine difference
  if (JSON.stringify(orderedOriginal) === JSON.stringify(orderedActual)) {
    $input.all()[0].json.github_status = "same";
  } else {
    $input.all()[0].json.github_status = "different";
    $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
  }
  $input.all()[0].json.content_decoded = orderedOriginal;

} else {
  // Order JSON object
  const n8nWorkflow = $input.all()[1].json;
  const orderedActual = orderJsonKeys(n8nWorkflow);

  // Proper formatting
  $input.all()[0].json.github_status = "new";
  $input.all()[0].json.n8n_data_stringy = JSON.stringify(orderedActual, null, 2);
}

// Return items
return $input.all();

const message = $json["message"]["text"];
const regex = /(https?:\/\/[^\s]+)/g;
const matches = message.match(regex);

return matches
  ? [{ json: { url: matches[0] } }]
  : [{ json: { Texto: message } }];

// Obtener el HTML
const htmlContent = $input.first().json["data"];

// Extraer todos los títulos y párrafos usando expresiones regulares
const titles = [];
const paragraphs = [];

// Extraer todos los títulos (H1, H2, H3, etc.)
const titleMatches = htmlContent.match(/<h[1-6][^>]*>(.*?)<\/h[1-6]>/g);
if (titleMatches) {
    for (let match of titleMatches) {
        titles.push(match.replace(/<[^>]*>/g, "")); // Eliminar etiquetas HTML
    }
}

// Extraer todos los párrafos
const paragraphMatches = htmlContent.match(/<p[^>]*>(.*?)<\/p>/g);
if (paragraphMatches) {
    for (let match of paragraphMatches) {
        paragraphs.push(match.replace(/<[^>]*>/g, "")); // Eliminar etiquetas HTML
    }
}
// Unir todos los títulos y párrafos en un solo texto
const content = "Títulos:\n" + titles.join("\n") + "\n\nPárrafos:\n" + paragraphs.join(" ");

// Limitar el número de caracteres para OpenAI (opcional)
//const maxCharacters = 10000;
let finalContent = content;
//if (finalContent.length > maxCharacters) {
//   finalContent = finalContent.slice(0, maxCharacters) + '...';
//}

// Retornar solo el contenido procesado y reducido
return [{ json: { content: finalContent } }];

const count = $json["n"];
const html = $json["html"];

let output = [];

for (let i = 0; i < count; i++) {
  output.push({ json: { html } });
}

return output;

const raw = $json["text"];

// Dividir líneas y limpiar cada una
const lines = raw
  .split('\n')
  .map(line => line.trim())
  .filter(line => line !== "");

// Extraer ideas que comienzan con "1." o "2." y limpiar el número
const ideas = lines
  .filter(line => /^\d\.\s*/.test(line)) // solo líneas que comienzan con número
  .map(line => line.replace(/^\d\.\s*/, "").trim()); // quitar el número

return ideas.map(idea => ({
  json: {
    IDEA: idea,
    FUENTE: "Agente",
    ESTADO: "Nueva"
  }
}));
