const mensaje = $json["quotedMessage"].conversation;

// Extraer fecha del pedido
const fechaRegex = /\((\d{2}\/\d{2}\/\d{4})\)/;
const fechaMatch = mensaje.match(fechaRegex);
const fechaPedido = fechaMatch ? fechaMatch[1] : null;

// Extraer pedidos y sus productos
const pedidos = [];
const pedidoRegex = /➡️ \*pedido N°: ([\w-]+)\*\n((?:- .+(?:\n|$))+)/g;
let match;

while ((match = pedidoRegex.exec(mensaje)) !== null) {
    const numeroPedido = match[1];
    const itemsTexto = match[2].trim();

    // Procesar cada producto del pedido
    const productos = itemsTexto.split('\n').map(item => {
        // Expresión regular mejorada para capturar todos los componentes
        const itemMatch = item.match(/^- (\d+) x (\d+) - ([^-]+) - ([^-]+) - (.+)$/);
        if (itemMatch) {
            return {
                NUM_PEDIDO: numeroPedido,
                PRODUCTO: itemMatch[3].trim(),
                CANT_PALETS: parseInt(itemMatch[1]),
                CANT_CAJAS: parseInt(itemMatch[2]),
                PALET: itemMatch[4].trim(),
                CAJAS: itemMatch[5].trim(),
            };
        }
        return null;
    }).filter(Boolean); // Eliminar posibles elementos null

    pedidos.push(...productos);
}

return {
    productos: pedidos,
    totalRegistros: pedidos.length,
    telefono: $json["Nº DE TELÉFONO"],
    FECHA: fechaPedido,
    PROVEEDOR_ID: $input.first().json.PROVEEDOR_ID,
    FINCA: $input.first().json.FINCA
};

