// Extraemos el bloque que está entre las etiquetas ```json y ```
const match = raw.match(/```json\s*([\s\S]*?)\s*```/);

if (!match) {
  throw new Error("No se encontró un bloque JSON válido.");
}

const cleanJsonString = match[1];
const parsed = JSON.parse(cleanJsonString);

// Devolver como nuevo item
return [{ json: parsed }];

// Paso 1: eliminar duplicados (por row_number)
const uniqueRows = [];
const seen = new Set();

for (const item of items) {
  const key = `${item.json.row_number}-${item.json.ID}-${item.json["NUM_PEDIDO"]}`;
  if (!seen.has(key)) {
    uniqueRows.push(item.json);
    seen.add(key);
  }
}

// Paso 2: preparar pedidos por proveedor (solo uno por producto, distinto al ya usado)
const pedidosPorProveedor = {};

for (const row of uniqueRows) {
  const cantidadTotal = row["CANT_PALETS"];
  const numPedido = row["NUM_PEDIDO"];
  const proveedorYaAsignado = row["PROVEEDOR_ID"]?.toString();

  if (cantidadTotal === 0) continue;

  // Obtener todos los proveedores posibles, excluyendo el ya usado
  const proveedores = Object.keys(row)
    .filter(key => key.startsWith("PROVEEDOR"))
    .map(key => row[key]?.toString())
    .filter(prov =>
      prov &&
      prov.trim() !== "" &&
      prov !== proveedorYaAsignado
    );

  // Seleccionar el primer proveedor disponible distinto al ya asignado
  const proveedorAsignado = proveedores[0];
  if (!proveedorAsignado) continue; // No hay otro proveedor disponible

  if (!pedidosPorProveedor[proveedorAsignado]) {
    pedidosPorProveedor[proveedorAsignado] = [];
  }

  pedidosPorProveedor[proveedorAsignado].push({
    row_number: row.row_number,
    ID_PRODUCTO: row.ID,
    NUM_PEDIDO: numPedido,
    PRODUCTO: row["PRODUCTOS"],
    CANT_PALETS: cantidadTotal,
    CAJAS: row["CAJAS"],
    CANT_CAJAS: row["CANT_CAJAS"],
    PALET: row["PALET"],
    CLIENTE: row["NOMBRE_CLIENTE"],
    CIF_CLIENTE: row["CIF_CLIENTE"],
    FECHA: row["FECHA"],
  });
}

// Paso 3: construir resultados
const results = [];

for (const [proveedorId, productos] of Object.entries(pedidosPorProveedor)) {
  if (productos.length > 0) {
    results.push({
      json: {
        PROVEEDOR_ID: proveedorId,
        PRODUCTOS: productos
      }
    });
  }
}

return results;

