const items = $input.all();

// Creamos un mapa para agrupar por proveedor
const agrupados = {};

for (const item of items) {
  const data = item.json;
  const proveedorId = data.PROVEEDOR_ID;

  // Si aún no existe, lo inicializamos
  if (!agrupados[proveedorId]) {
    agrupados[proveedorId] = {
      PROVEEDOR_ID: proveedorId,
      FINCA: data.FINCA,
      ENCARGADO: data["ENCARGADO "],
      TELEFONO: data["Nº DE TELÉFONO"],
      CLIENTE: data.CLIENTE,
      PRODUCTOS: [],
    };
  }

  // Agregamos el producto al array del proveedor
  agrupados[proveedorId].PRODUCTOS.push({
    NUM_PEDIDO: data.NUM_PEDIDO,
    FECHA: data.FECHA,
    ID_PRODUCTO: data.ID_PRODUCTO,
    PRODUCTOS: data.PRODUCTOS,
    CANT_PALETS: data.CANT_PALETS,
    CANT_CAJAS: data.CANT_CAJAS,
    PALET: data.PALET,
    CAJAS: data.CAJAS,
    ENTREGA: data.ENTREGA,
    STATUS: data.STATUS,
    comentarios: data.comentarios,
  });
}

// Convertimos el resultado en un array de items para n8n
return Object.values(agrupados).map(proveedor => ({ json: proveedor }));

// 1. Extraer y unir todos los productos válidos (CANT_PALETS > 0)
let productosValidos = [];

for (const item of items) {
  const proveedor = item.json;
  const productos = proveedor.PRODUCTOS || [];
  const status = "Solicitado";

  const filtrados = productos
    .filter(p => p.CANT_PALETS > 0)
    .map(p => ({
      ID_PRODUCTO: p.ID_PRODUCTO,
      PROVEEDOR_ID: proveedor.PROVEEDOR_ID,
      FINCA: proveedor.FINCA,
      NUM_PEDIDO: p.NUM_PEDIDO,
      PRODUCTO: p.PRODUCTOS,
      PALET: p.PALET,
      CAJAS: p.CAJAS,
      STATUS: status,
    }));

  productosValidos.push(...filtrados);
}

// 2. Eliminar duplicados por combinación ID_PRODUCTO + NUM_PEDIDO 
const vistos = new Set();
const productosUnicos = productosValidos.filter(p => {
  const clave = `${p.NUM_PEDIDO}-${p.PRODUCTO}`;
  if (vistos.has(clave)) return false;
  vistos.add(clave);
  return true;
});

// 3. Devolver los productos únicos
return productosUnicos.map(p => ({ json: p }));

