// --- CONFIGURACI√ìN ---
// Se usa el m√≥dulo 'url', que est√° permitido en n8n, para manejar las URLs.
const urlModule = require('url');

const MAIN_DOMAIN = String($('Table Name').first().json.dominio || '').trim();
const BASE_URL = String($('Table Name').first().json.url || '').trim();
const tableName = $('Table Name').first().json.tableName
const chat = $('Table Name').first().json.chat

if (!MAIN_DOMAIN || !BASE_URL.startsWith('http')) {
  throw new Error(`MAIN_DOMAIN ("${MAIN_DOMAIN}") o BASE_URL ("${BASE_URL}") son inv√°lidos.`);
}

// --------------------

const htmlString = $input.first().json.data;

const cleanText = (text) => {
  if (!text || typeof text !== 'string') return '';
  let clean = text.replace(/<[^>]+>/g, '');
  return clean.replace(/\s+/g, ' ').trim();
};

const plainText = htmlString
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '')
  .replace(/<head[\s\S]*?<\/head>/gi, '')
  .replace(/<noscript[\s\S]*?<\/noscript>/gi, '')
  .replace(/<svg[\s\S]*?<\/svg>/gi, '')
  .replace(/<[^>]+>/g, '\n')
  .replace(/\n\s*\n/g, '\n')
  .replace(/\s{2,}/g, ' ')
  .trim();

const urlRegex = /<a[^>]*href=["']([^"']+)["'][^>]*>(.*?)<\/a>/gis;

const excludeExtensions = [
    '.jpg', '.jpeg', '.png', '.gif', '.svg', '.webp', '.pdf', '.doc',
    '.docx', '.xls', '.xlsx', '.zip', '.rar', '.csv', '.woff2', '.ttf'
];

// --- PROCESAMIENTO CON EL M√ìDULO 'url' ---
const matches = [...htmlString.matchAll(urlRegex)];

const allLinks = matches.map(([_, href, text]) => {
  if (!href) return null;
  // Usamos url.resolve(base, ruta) en lugar de new URL(ruta, base)
  const absoluteUrl = urlModule.resolve(BASE_URL, href.trim());
  return {
    nombre: cleanText(text),
    url: absoluteUrl.split('#')[0],
  };
});

const filteredLinks = allLinks.filter(link => {
  if (!link || !link.url || !link.nombre || link.url === BASE_URL) {
    return false;
  }
  const lowerUrl = link.url.toLowerCase();
  const path = lowerUrl.split('?')[0];

  if (lowerUrl.startsWith('mailto:') || lowerUrl.startsWith('tel:') || excludeExtensions.some(ext => path.endsWith(ext))) {
    return false;
  }

  // Usamos url.parse() para obtener las partes de la URL, como el hostname
  const parsedUrl = urlModule.parse(link.url);
  return parsedUrl.hostname && parsedUrl.hostname.endsWith(MAIN_DOMAIN);
});

const uniqueUrls = Array.from(new Map(filteredLinks.map(item => [item.url, item])).values());

return [{
  json: {
    MAIN_DOMAIN,
    BASE_URL,
    tableName: tableName,
    chat: chat,
    total_matches: matches.length,
    urls_encontradas: uniqueUrls.length,
    urls: uniqueUrls,
    contenido: plainText
  }
}];

/*
  Este nodo prepara todo para empezar.
  Crea un 'estado' inicial que se pasar√° a trav√©s del flujo.
*/

const startUrl = $('Table Name').first().json.url;
const urls = $input.first().json.urls.map((url) => {
  return `${url.url}`
})

const initialState = {
  // La lista de URLs que faltan por visitar (nuestra cola de trabajo)
  urls_to_visit: [startUrl, ...urls],

  // Un array de URLs que ya hemos procesado para no repetirlas
  visited_urls: [], // <-- ESTE ES EL CAMBIO M√ÅS IMPORTANTE

  // Aqu√≠ guardaremos todo el contenido que extraigamos
  all_content: [],

  // La URL que estamos procesando en la iteraci√≥n actual
  current_url: null
};

return initialState;

/*
  Este nodo act√∫a como el gestor de la cola.
  1. Toma la siguiente URL de la lista 'urls_to_visit'.
  2. La elimina de la cola.
  3. La a√±ade a la lista 'visited_urls' para no volver a procesarla.
  4. Pasa la URL actual al siguiente nodo para la petici√≥n HTTP.
*/

const state = $input.first().json;

// Sacamos la primera URL de la cola de trabajo
const nextUrl = state.urls_to_visit.shift();

// La a√±adimos al registro de visitadas (usando .push() para el array)
state.visited_urls.push(nextUrl);

// La establecemos como la URL actual para esta iteraci√≥n
state.current_url = nextUrl;

return state;

// ‚úÖ Importamos el m√≥dulo 'url', que est√° permitido en n8n, para manejar las URLs.
const urlModule = require('url');

// --- CONFIGURACI√ìN ---
const MAIN_DOMAIN = String($('Table Name').first().json.dominio || '').trim();
const BASE_URL = String($('Table Name').first().json.url || '').trim();
const tableName = $('Table Name').first().json.tableName
const chat = $('Table Name').first().json.chat

if (!MAIN_DOMAIN || !BASE_URL.startsWith('http')) {
  throw new Error(`MAIN_DOMAIN ("${MAIN_DOMAIN}") o BASE_URL ("${BASE_URL}") son inv√°lidos.`);
}

// --- ESTADO Y DATOS DE ENTRADA ---
const state = $("3. Get Next URL from Queue1").first().json;
const htmlString = $input.first().json.data;

if (!htmlString) {
    return [{ json: state }]; // Si no hay HTML, devolvemos el estado sin cambios
}

const visitedUrlsSet = new Set(state.visited_urls || []);

// --- LIMPIEZA DEL CONTENIDO ---
const plainText = htmlString
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '')
  .replace(/<head[\s\S]*?<\/head>/gi, '')
  .replace(/<nav[\s\S]*?<\/nav>/gi, '')
  .replace(/<header[\s\S]*?<\/header>/gi, '')
  .replace(/<footer[\s\S]*?<\/footer>/gi, '')
  .replace(/<svg[\s\S]*?<\/svg>/gi, '')
  .replace(/<[^>]+>/g, '\n')
  .replace(/\n\s*\n/g, '\n')
  .replace(/\s{2,}/g, ' ')
  .trim();

state.all_content.push({
  source: state.current_url,
  content: plainText,
});


// --- EXTRACCI√ìN Y FILTRADO DE NUEVOS ENLACES ---
const urlRegex = /<a[^>]*href=["']([^"']+)["'][^>]*>/gi;
const matches = [...htmlString.matchAll(urlRegex)];
const excludeExtensions = ['.jpg', '.png', '.gif', '.zip', '.pdf', '.svg', '.ico', '.webp'];
const newLinksSet = new Set();

const normalizeUrl = (urlString) => {
  if (!urlString) return null;
  const parsedUrl = urlModule.parse(urlString);
  parsedUrl.hash = null;
  if (parsedUrl.pathname && parsedUrl.pathname !== '/' && parsedUrl.pathname.endsWith('/')) {
      parsedUrl.pathname = parsedUrl.pathname.slice(0, -1);
  }
  return urlModule.format(parsedUrl);
};

for (const [, href] of matches) {
  if (!href) continue;
  const fullUrl = urlModule.resolve(state.current_url, href.trim());
  const normalized = normalizeUrl(fullUrl);

  if (
    normalized &&
    normalized.startsWith(BASE_URL) &&
    !excludeExtensions.some(ext => normalized.toLowerCase().endsWith(ext)) &&
    !visitedUrlsSet.has(normalized)
  ) {
    newLinksSet.add(normalized);
  }
}

// --- ACTUALIZACI√ìN DEL ESTADO ---
const combinedUrls = [...new Set([...state.urls_to_visit, ...newLinksSet])];
state.urls_to_visit = combinedUrls.filter(url => !visitedUrlsSet.has(url));
state.visited_urls = Array.from(visitedUrlsSet);

// --- SALIDA CON CONTADORES ---
// En lugar de devolver solo el estado, devolvemos un objeto JSON que incluye
// el estado (para que el bucle siga funcionando) y las nuevas cuentas.
const finalJsonOutput = {

  // A√±adimos las cuentas de esta ejecuci√≥n
  debug_info: {
    MAIN_DOMAIN: MAIN_DOMAIN,
    tableName: tableName,
    chat: chat,
    page_processed: state.current_url,

    total_matches_on_page: matches.length,
    new_urls_found_on_page: newLinksSet.size,
    total_urls_in_queue: state.urls_to_visit.length,
    total_urls_visited: state.visited_urls.length
  },
    ...state // Mantenemos todas las propiedades del estado (urls_to_visit, etc.)

};

return [{ json: finalJsonOutput }];

const state = $('5. Extract Content & Find New Links1').first().json;
const tableName = $('data').first().json.tableName;
const chatID = $('data').first().json.chat.id;
const name = $('data').first().json.chat.first_name;

// Condici√≥n de salida: Si ya no hay URLs para visitar, el proceso ha terminado.
// Pasamos el estado final con todo el contenido acumulado.
if (state.urls_to_visit.length === 0 && state.all_content?.length > 0) {
  const finalState = {
    ...state,
    tableName,
    chatID,
    name
  };
  return [{ json: finalState }];
}

// Condici√≥n de bucle: Si todav√≠a hay URLs para visitar.
// Creamos una copia del estado para no modificar el original directamente.
const stateForNextPage = { ...state };

// ‚úÖ Limpiamos el array 'all_content', dej√°ndolo vac√≠o para la siguiente iteraci√≥n.
stateForNextPage.all_content = [];

// Devolvemos el estado modificado.
return [{ json: stateForNextPage }];

