const MAX_LENGTH = 1950; // L√≠mite para Telegram MarkdownV2

function escapeMarkdownV2(text) {
  return text
    .replace(/\\/g, '\\\\')
    .replace(/_/g, '\\_')
    .replace(/\*/g, '\\*')
    .replace(/\[/g, '\\[')
    .replace(/\]/g, '\\]')
    .replace(/\(/g, '\\(')
    .replace(/\)/g, '\\)')
    .replace(/~/g, '\\~')
    .replace(/>/g, '\\>')
    .replace(/#/g, '\\#')
    .replace(/\+/g, '\\+')
    .replace(/-/g, '\\-')
    .replace(/=/g, '\\=')
    .replace(/\|/g, '\\|')
    .replace(/{/g, '\\{')
    .replace(/}/g, '\\}')
    .replace(/\./g, '\\.')
    .replace(/!/g, '\\!');
}

// Limpia encabezados y formatos no compatibles
function limpiarTexto(text) {
  return text
    .replace(/^\s*[*]\s*/gm, '- ')                          // Convertir listas con * a -
    .replace(/^#{1,6}\s*/gm, '')                             // Remover encabezados ### o ##
    .replace(/^[-]{3,}$/gm, '---')                           // Normalizar separadores
    .replace(/^(\s*)([*_]{1,3})(.*?)\2\s*$/gm, '$1*$3*')     // Reforzar √©nfasis sin exceso
    .replace(/\\`\\`\\`bash/g, '```bash')                    // Arreglar escapes accidentales
    .replace(/```bash\n(\s*)/g, '```bash\n');                // Eliminar indentaci√≥n innecesaria en bloque
}

function splitMarkdownPreservingCodeBlocks(text) {
  const partes = [];
  let buffer = '';
  const bloques = text.split(/(```[\s\S]*?```)/g); // Separar bloques de c√≥digo

  for (const bloque of bloques) {
    const esBloqueCodigo = bloque.startsWith('```');
    const contenido = esBloqueCodigo
      ? bloque
      : escapeMarkdownV2(limpiarTexto(bloque));

    const lineas = contenido.split('\n');
    for (const linea of lineas) {
      if ((buffer + '\n' + linea).length > MAX_LENGTH) {
        partes.push(buffer.trim());
        buffer = linea;
      } else {
        buffer += '\n' + linea;
      }
    }
  }
  if (buffer.trim()) partes.push(buffer.trim());
  return partes;
}

// === 1. Obtener salida cruda ===
const rawOutput = items[0].json.message || '';
if (!rawOutput || typeof rawOutput !== 'string') {
  return [{ json: { message: '‚ö†Ô∏è No se encontr√≥ texto para enviar.' } }];
}

// === 2. Normalizaci√≥n b√°sica antes del escape ===
const preformateado = limpiarTexto(rawOutput);

// === 3. Escapar y dividir preservando bloques de c√≥digo ===
const partes = splitMarkdownPreservingCodeBlocks(preformateado);

// === 4. Devolver mensajes formateados ===
return partes.map((texto, index) => ({
  json: {
    message: texto,
    index: index + 1,
    total: partes.length
  }
}));

const MAX_LENGTH = 3900;

let rawOutput = items[0].json.output || '';
if (!rawOutput || typeof rawOutput !== 'string') {
  return [{ json: { message: '‚ö†Ô∏è lo siento, hubo un error, puedes volver a intentarlo' } }];
}

// Proteger bloques de c√≥digo (```...```)
const bloques = [];
rawOutput = rawOutput.replace(/```([\s\S]*?)```/g, (match, contenido, index) => {
  const key = `__BLOQUE_CODE_${bloques.length}__`;
  bloques.push(contenido);
  return key;
});

// Reemplazos globales fuera de los bloques de c√≥digo
rawOutput = rawOutput.replace(/\\([._*[\]()~`>#+\-=|{}!])/g, '$1'); // des-escape markdown
rawOutput = rawOutput.replace(/"/g, "'"); // comillas dobles por simples

// Restaurar los bloques de c√≥digo
bloques.forEach((contenido, i) => {
  rawOutput = rawOutput.replace(`__BLOQUE_CODE_${i}__`, `\`\`\`\n${contenido}\n\`\`\``);
});

const partes = [];
let texto = rawOutput;

while (texto.length > 0) {
  if (texto.length <= MAX_LENGTH) {
    partes.push(texto.trim());
    break;
  }

  let fragmento = texto.slice(0, MAX_LENGTH);

  // Buscar punto de corte: primero lista numerada, luego ###, luego punto, luego salto de l√≠nea, luego espacio
  let corte =
    fragmento.lastIndexOf('\n1.') >= 0 ? fragmento.lastIndexOf('\n1.') :
    fragmento.match(/\n\d+\./g) ? fragmento.lastIndexOf(fragmento.match(/\n\d+\./g).slice(-1)[0]) :
    fragmento.lastIndexOf('\n### ') >= 0 ? fragmento.lastIndexOf('\n### ') + 1 :
    fragmento.lastIndexOf('. ') >= 0 ? fragmento.lastIndexOf('. ') + 1 :
    fragmento.lastIndexOf('\n') >= 0 ? fragmento.lastIndexOf('\n') + 1 :
    fragmento.lastIndexOf(' ');

  if (corte <= 0) corte = MAX_LENGTH;

  let parte = fragmento.slice(0, corte).trim();

  // üîπ Eliminar los encabezados ### al final del fragmento
  parte = parte.replace(/^###\s*/gm, '');

  partes.push(parte);
  texto = texto.slice(corte).trim();
}

// Al final, devolver los mensajes sin encabezados ###
return partes.map((parte, index) => ({
  json: {
    message: parte,
    index: index + 1,
    total: partes.length,
  }
}));
