    ];

    // Fecha y hora actual en Bogot√°
    const ahoraBogota = new Date(
        new Date().toLocaleString("en-US", { timeZone: "America/Bogota" })
    );

    const diaActual = diasSemana[ahoraBogota.getDay()];
    const minutosActuales = ahoraBogota.getHours() * 60 + ahoraBogota.getMinutes();

    let enviarHoy = false;

    // Validar frecuencia
    if (data.tipo_frecuencia === "DIARIO") {
        enviarHoy = true;
    } else if (data.tipo_frecuencia === "DIAS_ESPECIFICOS" && Array.isArray(data.dias_semana)) {
        enviarHoy = data.dias_semana.includes(diaActual);
    }

    // Validar hora con tolerancia de ¬±20 minutos
    let enviarAhora = false;
    if (enviarHoy && Array.isArray(data.horas_envio)) {
        enviarAhora = data.horas_envio.some(hora => {
            const [h, m] = hora.split(":").map(Number);
            const minutosProgramados = h * 60 + m;
            return Math.abs(minutosActuales - minutosProgramados) <= 30;
        });
    }

    results.push({
        json: {
            ...data,
            hoy_es: diaActual,
            hora_actual: ahoraBogota.toTimeString().slice(0, 5),
            enviarHoy,
            enviarAhora
        }
    });
});

return results;

const usuarios = $json; // Usuario actual del loop
const anuncios = $items("enviar?", 0).map(item => item.json);
const logs = $items("Obtener Logs Recientes", 0).map(item => item.json);

// Aseguramos que sean arrays
const arrUsuarios = Array.isArray(usuarios) ? usuarios : [usuarios];
const arrAnuncios = Array.isArray(anuncios) ? anuncios : [anuncios];
const arrLogs = Array.isArray(logs) ? logs : [logs];

const result = arrUsuarios.map(usuario => {
  // Filtramos logs que correspondan a este usuario
  const logsUsuario = arrLogs.filter(
    log => log.id_usuario_db === usuario.id
  );

  // Anexamos todos los anuncios disponibles
  return {
    ...usuario,
    anuncios_disponibles: arrAnuncios,
    logs: logsUsuario
  };
});

return result.map(r => ({ json: r }));

