// Accedemos a los datos del nodo anterior. Usamos [0] porque los datos vienen en un array.
const informe = $input.first().json.informe_data;

// Extraemos los totales de gastos del per√≠odo actual y anterior
const gastosHoy = informe.resumen.actual.totalGastos;
const gastosAyer = informe.resumen.anterior.totalGastos;

// Creamos la configuraci√≥n para un gr√°fico de barras
const chartConfig = {
  type: 'bar',
  data: {
    labels: ['Gastos de Ayer', 'Gastos de Hoy'],
    datasets: [{
      label: 'Total Gastado',
      data: [gastosAyer, gastosHoy],
      backgroundColor: ['rgba(255, 99, 132, 0.5)', 'rgba(54, 162, 235, 0.5)'],
      borderColor: ['rgb(255, 99, 132)', 'rgb(54, 162, 235)'],
      borderWidth: 1
    }]
  },
  options: {
    title: {
      display: true,
      text: 'Comparaci√≥n de Gastos Diarios'
    }
  }
};

// Devolvemos la configuraci√≥n para el siguiente nodo
return { chartConfig };

const informe = $input.first().json.informe_data;

// Extraemos las categor√≠as de gastos de hoy
const categoriasHoy = informe.categorias.actual;

// Preparamos los datos para el gr√°fico
const labels = categoriasHoy.map(cat => cat.nombre);
const dataPoints = categoriasHoy.map(cat => cat.total);

// Creamos la configuraci√≥n para un gr√°fico de dona (doughnut)
const chartConfig = {
  type: 'doughnut',
  data: {
    labels: labels,
    datasets: [{
      data: dataPoints,
      // Puedes agregar m√°s colores si tienes m√°s categor√≠as
      backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'], 
    }]
  },
  options: {
    title: {
      display: true,
      text: 'Distribuci√≥n de Gastos de Hoy'
    }
  }
};

return { chartConfig };

const informe = $input.first().json.informe_data;
const categoriasHoy = informe.categorias.actual;

// Transformamos los datos al formato que necesita el treemap
const treeData = [];
categoriasHoy.forEach(cat => {
  cat.desglose.forEach(item => {
    treeData.push({
      categoria: cat.nombre, // Nivel 1 de agrupaci√≥n
      descripcion: item.descripcion, // Nivel 2 de agrupaci√≥n
      monto: item.montoTotal // El valor que define el tama√±o del √°rea
    });
  });
});

// Creamos la configuraci√≥n para el treemap
const chartConfig = {
  type: 'treemap',
  data: {
    datasets: [{
      label: 'Desglose de Gastos',
      tree: treeData,
      key: 'monto', // La propiedad que contiene el valor num√©rico
      groups: ['categoria', 'descripcion'], // Las propiedades para agrupar
      backgroundColor: (ctx) => { // L√≥gica para colorear por categor√≠a
        if (ctx.type !== 'node') return;
        if (ctx.raw.g === 'Alimentaci√≥n') return '#FF6384';
        if (ctx.raw.g === 'Transporte') return '#36A2EB';
        return '#FFCE56';
      }
    }]
  },
  options: {
    title: {
      display: true,
      text: 'Desglose Detallado de Gastos de Hoy'
    }
  }
};

return { chartConfig };
