const ACCOUNT_ID = $input.first().json.body.account.id;
const BASE_URL = `https://crm.whitelabel.lat/api/v1/accounts/${ACCOUNT_ID}`;
const TOKEN = "uuWT9PNsY83ucax7HeqBEUSi";

// Tomamos el evento entrante
const input = $input.first().json;
const conversationId = input.body.conversation.id;
const replyToId = input.body.content_attributes?.in_reply_to;

async function getMessage(conversationId, messageId) {
  const url = `${BASE_URL}/conversations/${conversationId}/messages?before=${messageId + 1}`;

  const response = await this.helpers.httpRequest({
    method: "GET",
    url,
    headers: {
      "api_access_token": TOKEN,
      "Content-Type": "application/json",
    },
    json: true,
  });

  const messages = response.payload || [];
  if (!Array.isArray(messages) || messages.length === 0) {
    return null;
  }

  return messages.find(m => m.id === messageId) || null;
}

let replyMessage = null;

if (replyToId) {
  replyMessage = await getMessage(conversationId, replyToId);
}

// Clonamos el input para no perder el formato original
const output = JSON.parse(JSON.stringify(input));

// Si existe un reply, lo agregamos dentro del body
if (replyMessage) {
  output.body.reply_message = replyMessage;
} else {
  // Si no hay reply, mantenemos la estructura pero con content null
  output.body.reply_message = { content: null };
}

return [ { json: output } ];
