// Entrada esperada: array con [ bancoPreguntas, respuestasUsuario ]

const [banco, respuestas] = items.map(i => i.json);

const resultado = {};

// Recorremos las secciones principales
["Retratar", "Descomponer", "Reinterpretar"].forEach(seccion => {
  resultado[seccion] = {};

  // Recorremos cada pregunta dentro del banco de preguntas
  for (const [key, value] of Object.entries(banco[seccion] || {})) {
    const pregunta = value.Pregunta;

    // Buscamos la respuesta del usuario en la segunda parte
    const respuestaUsuario = respuestas[seccion]?.[pregunta] || "";

    resultado[seccion][key] = {
      ...value,
      "respuesta del usuario": respuestaUsuario
    };
  }
});

// Añadimos también los datos del usuario
resultado["userData"] = respuestas.userData || {};

return [{ json: resultado }];

// n8n Code node
// Input esperado: items[0].json.data

const input = items[0].json.data;

// Secciones permitidas por ahora
const seccionesValidas = ["Retratar", "Descomponer", "Reinterpretar"];

const resultado = {};

input.forEach((item) => {
  const seccion = item["Sección"]?.trim();
  const pregunta = item["Pregunta"]?.trim();

  // Filtrar: debe tener sección válida y pregunta
  if (!seccion || !pregunta || !seccionesValidas.includes(seccion)) {
    return;
  }

  // Crear sección si no existe
  if (!resultado[seccion]) {
    resultado[seccion] = {};
  }

  // Solo incluir campos con contenido
  const detalle = {};
  if (pregunta) detalle["Pregunta"] = pregunta;
  if (item["Opción A"]) detalle["Opción A"] = item["Opción A"];
  if (item["Puntos A"]) detalle["Puntos A"] = item["Puntos A"];
  if (item["Opción B"]) detalle["Opción B"] = item["Opción B"];
  if (item["Puntos B"]) detalle["Puntos B"] = item["Puntos B"];
  if (item["Opción C"]) detalle["Opción C"] = item["Opción C"];
  if (item["Puntos C"]) detalle["Puntos C"] = item["Puntos C"];

  // Guardar con row_number como clave
  resultado[seccion][`Pregunta ${item.row_number-1}`] = detalle;
});

return [
  {
    json: resultado
  }
];

