// Sanea strings que contienen JSON con errores comunes (p. ej. comillas sin escapar dentro de valores).
// Si logra parsear → devuelve objeto/array.
// Si no logra parsear → deja el valor original como string (seguro).

function fixInvalidQuotes(jsonLike) {
  const s = jsonLike;
  const out = [];
  let i = 0;
  const len = s.length;

  function skipSpaces(idx) {
    while (idx < len && /\s/.test(s[idx])) idx++;
    return idx;
  }

  let inValueString = false;

  while (i < len) {
    const ch = s[i];

    if (!inValueString) {
      // Copiamos normal, pero si vemos ":" seguido de comillas -> comienza un valor string
      if (ch === ':') {
        out.push(ch);
        i++;
        let j = skipSpaces(i);
        if (j < len && s[j] === '"') {
          // añadimos los espacios intermedios + comilla inicial del valor
          out.push(s.slice(i, j + 1));
          i = j + 1; // nos colocamos después de la comilla inicial del valor
          inValueString = true;
          continue;
        } else {
          continue;
        }
      } else {
        out.push(ch);
        i++;
        continue;
      }
    } else {
      // Estamos dentro de un valor string (entre comillas después de un ':')
      const c = s[i];

      if (c === '\\') {
        // Mantener escapes existentes tal cual
        if (i + 1 < len) {
          out.push(c, s[i + 1]);
          i += 2;
        } else {
          out.push(c);
          i++;
        }
        continue;
      }

      if (c === '"') {
        // ¿Es comilla de cierre del valor o comilla interna que rompe JSON?
        let k = skipSpaces(i + 1);
        const next = s[k] || '';
        if (next === ',' || next === '}' || next === ']' ) {
          // Cierra el valor
          out.push('"');
          i++;
          inValueString = false;
          continue;
        } else {
          // Comilla interna dentro del contenido → escaparla
          out.push('\\"');
          i++;
          continue;
        }
      }

      // Carácter normal dentro del valor
      out.push(c);
      i++;
    }
  }

  return out.join('');
}

function sanitizeJsonLike(str) {
  if (typeof str !== 'string') return str;
  let s = str.trim();

  // Si no parece JSON, no tocar
  if (!(s.startsWith('{') || s.startsWith('['))) return str;

  // Quitar BOM si existe
  s = s.replace(/^\uFEFF/, '');

  // Intento directo
  try { return JSON.parse(s); } catch (e) {}

  // Quitar comas colgantes
  let t = s.replace(/,\s*([}\]])/g, '$1');
  try { return JSON.parse(t); } catch (e) {}

  // Reparar comillas internas en valores string
  const fixed = fixInvalidQuotes(t);
  try { return JSON.parse(fixed); } catch (e) {}

  // Último recurso: devolver string (seguro)
  return str;
}

function deepSanitize(obj) {
  if (obj === null || obj === undefined) return obj;
  if (typeof obj === 'string') return sanitizeJsonLike(obj);
  if (Array.isArray(obj)) return obj.map(deepSanitize);
  if (typeof obj === 'object') {
    const out = {};
    for (const [k, v] of Object.entries(obj)) {
      out[k] = deepSanitize(v);
    }
    return out;
  }
  return obj;
}

return items.map(item => {
  const clean = deepSanitize(item.json);
  return { json: clean };
});

