// Paso 1: separar stock del input
let stockData = [];
let pedidosData = [];

for (const item of items) {
  if (item.json.Stock) {
    stockData = item.json.Stock;
  } else {
    pedidosData.push(item.json);
  }
}

// Crear un mapa de stock por producto
const stockMap = {};
for (const s of stockData) {
  stockMap[s.PRODUCTOS.trim().toUpperCase()] = {
    disponible: parseInt(s.CANT_PALET) || 0,
    cajas: parseInt(s.CANT_CAJAS) || 0,
    row_number: s.row_number,
    detalle: s
  };
}

// Paso 2: eliminar duplicados (por row_number + pedido)
const uniqueRows = [];
const seen = new Set();

for (const row of pedidosData) {
  const key = `${row.row_number}-${row.ID}-${row["NUM_PEDIDO"]}`;
  if (!seen.has(key)) {
    uniqueRows.push(row);
    seen.add(key);
  }
}

// Paso 3: preparar pedidos por proveedor
const pedidosPorProveedor = {};

function agregarPedido(proveedorId, row, cantidad, productoKey = null, stockRow = null) {
  if (!pedidosPorProveedor[proveedorId]) {
    pedidosPorProveedor[proveedorId] = [];
  }

  let restantePalets = null;
  let restanteCajas = null;

  if (proveedorId === "STOCK" && productoKey && stockRow) {
    const disponiblePalets = stockMap[productoKey].disponible;
    const disponibleCajas = stockMap[productoKey].cajas;

    const totalCajas = parseInt(row["CANT_CAJAS"]) || 0;
    const totalPalets = parseInt(row["CANT_PALETS"]) || 0;

    restantePalets = disponiblePalets;
    restanteCajas =
      totalPalets > 0
        ? Math.floor((disponiblePalets * totalCajas) / totalPalets)
        : disponibleCajas;
  }

  pedidosPorProveedor[proveedorId].push({
    row_number_pedido: row.row_number,
    row_number_stock: stockRow || null,
    ID_PRODUCTO: row.ID,
    NUM_PEDIDO: row["NUM_PEDIDO"],
    PRODUCTO: row["PRODUCTOS"],
    CANT_PALETS: cantidad,
    CAJAS: row["CAJAS"],
    CANT_CAJAS: row["CANT_CAJAS"],
    PALET: row["PALET"],
    CLIENTE: row["NOMBRE_CLIENTE"],
    CIF_CLIENTE: row["CIF_CLIENTE"],
    FECHA: row["FECHA"],
    ...(proveedorId === "STOCK"
      ? {
          CANT_PALETS_RESTANTES: restantePalets,
          CANT_CAJAS_RESTANTES: restanteCajas
        }
      : {})
  });
}

// Procesar pedidos
for (const row of uniqueRows) {
  let cantidadTotal = parseInt(row["CANT_PALETS"]) || 0;
  if (cantidadTotal === 0) continue;

  const productoKey = row["PRODUCTOS"].trim().toUpperCase();

  // Paso 3.1: revisar stock
  if (stockMap[productoKey] && stockMap[productoKey].disponible > 0) {
    const disponible = stockMap[productoKey].disponible;
    const usado = Math.min(cantidadTotal, disponible);

    // Restar stock
    stockMap[productoKey].disponible -= usado;

    // Agregar asignación al STOCK
    if (usado > 0) {
      agregarPedido("STOCK", row, usado, productoKey, stockMap[productoKey].row_number);
      cantidadTotal -= usado;
    }
  }

  // Paso 3.2: si aún falta, repartir entre proveedores
  if (cantidadTotal > 0) {
    const proveedores = [];

    for (const key of Object.keys(row)) {
      const keyNorm = key.toString().trim().toUpperCase().replace(/\s+/g, " ");
      if (keyNorm.startsWith("PROVEEDOR")) {
        const indexMatch = keyNorm.match(/PROVEEDOR\s*(\d+)/);
        if (indexMatch) {
          const index = indexMatch[1];
          const proveedorId = row[key]?.toString().trim();
          if (proveedorId) {
            const porcentajeKey = Object.keys(row).find(k => {
              const kNorm = k.toString().trim().toUpperCase().replace(/\s+/g, " ");
              return kNorm === `% ${index}` || kNorm === `%${index}`;
            });
            const porcentaje = porcentajeKey ? parseFloat(row[porcentajeKey]) : null;
            proveedores.push({
              proveedorId,
              porcentaje: isNaN(porcentaje) ? null : porcentaje,
              orden: parseInt(index) || 0
            });
          }
        }
      }
    }

    if (proveedores.length === 0) continue;

    const conPorcentaje = proveedores.filter(p => p.porcentaje !== null);
    const sinPorcentaje = proveedores.filter(p => p.porcentaje === null);

    let asignaciones = [];

    if (conPorcentaje.length > 0) {
      let totalAsignado = 0;
      for (const p of conPorcentaje) {
        p.cantidad = Math.floor((cantidadTotal * p.porcentaje) / 100);
        totalAsignado += p.cantidad;
      }

      let restante = cantidadTotal - totalAsignado;
      const ordenPrioridad = [...conPorcentaje]
        .sort((a, b) => b.porcentaje - a.porcentaje || a.orden - b.orden);

      for (let i = 0; i < restante; i++) {
        ordenPrioridad[i % ordenPrioridad.length].cantidad += 1;
      }

      asignaciones = [...conPorcentaje];

      if (sinPorcentaje.length > 0) {
        const restanteLibre = cantidadTotal - asignaciones.reduce((s, p) => s + p.cantidad, 0);
        const cantPorProveedor = Math.floor(restanteLibre / sinPorcentaje.length);
        let sobrante = restanteLibre % sinPorcentaje.length;

        for (const p of sinPorcentaje) {
          p.cantidad = cantPorProveedor + (sobrante > 0 ? 1 : 0);
          if (sobrante > 0) sobrante--;
        }

        asignaciones = [...asignaciones, ...sinPorcentaje];
      }

    } else {
      const cantidadPorProveedor = Math.floor(cantidadTotal / proveedores.length);
      let resto = cantidadTotal % proveedores.length;

      for (const p of proveedores) {
        p.cantidad = cantidadPorProveedor + (resto > 0 ? 1 : 0);
        if (resto > 0) resto--;
      }

      asignaciones = [...proveedores];
    }

    for (const asignacion of asignaciones) {
      if (asignacion.cantidad > 0) {
        agregarPedido(asignacion.proveedorId, row, asignacion.cantidad);
      }
    }
  }
}

// Paso 4: construir resultados de pedidos
const results = [];
for (const [proveedorId, productos] of Object.entries(pedidosPorProveedor)) {
  results.push({
    json: {
      PROVEEDOR_ID: proveedorId,
      PRODUCTOS: productos
    }
  });
}

return results;

const results = [];
const zonaHoraria = 'America/Bogota'; //cambiar el pais
const ahora = new Date(new Date().toLocaleString('en-US', { timeZone: zonaHoraria }));

// Función para formatear fecha como "DD/MM/YYYY"
function formatearFecha(fecha) {
  const dia = String(fecha.getDate()).padStart(2, '0');
  const mes = String(fecha.getMonth() + 1).padStart(2, '0');
  const año = fecha.getFullYear();
  return `${dia}/${mes}/${año}`;
}

// Fecha de hoy
const fechaHoy = formatearFecha(ahora);

// Fecha de mañana (hoy + 1 día)
const manana = new Date(ahora);
manana.setDate(manana.getDate() + 1);
const fechaManana = formatearFecha(manana);

// Determinar saludo y entrega
const hora = ahora.getHours();
const esTarde = hora >= 17;

const saludo = hora < 12 ? 'Buen día' : 'Buena tarde';
const entrega = esTarde ? `mañana - (${fechaManana})` : `hoy - (${fechaHoy})`;
const fechaentrega = esTarde ? `${fechaManana}` : `${fechaHoy}`;

for (const item of items) {
  const productos = item.json.PRODUCTOS;
  const productosPorPedido = {};

  for (const prod of productos) {
    if (prod.CANT_PALETS > 0) {
      const numPedido = prod.NUM_PEDIDO || 'SIN_PEDIDO';
      if (!productosPorPedido[numPedido]) {
        productosPorPedido[numPedido] = [];
      }

      productosPorPedido[numPedido].push(
        `- ${prod.CANT_PALETS} x ${prod.CANT_CAJAS} - ${prod.PRODUCTO} - ${prod.PALET} - ${prod.CAJAS}`
      );
    }
  }

  if (Object.keys(productosPorPedido).length > 0) {
    let mensajeProductos = `${saludo}, para ${entrega}\n\n`;

    for (const [numPedido, listaProductos] of Object.entries(productosPorPedido)) {
      mensajeProductos += `➡️ *pedido N°: ${numPedido}*\n`;
      mensajeProductos += listaProductos.join('\n\n') + '\n\n';
    }

    mensajeProductos = mensajeProductos.trim();

    results.push({
      json: {
        ...item.json,
        MENSAJE_PROVEEDOR: mensajeProductos,
        FECHA: fechaentrega,
        ENTREGA: entrega
      }
    });
  } else {
    results.push({
      json: {
        ...item.json,
        MENSAJE_PROVEEDOR: '',
        FECHA: fechaHoy
      }
    });
  }
}

return results;

// 1. Extraer y unir todos los productos válidos (CANT_PALETS > 0)
let productosValidos = [];

for (const item of items) {
  const proveedor = item.json;
  const productos = proveedor.PRODUCTOS || [];
  const status = "Aprobacion";

  const filtrados = productos
    .filter(p => p.CANT_PALETS > 0)
    .map(p => ({
      row_number: p.row_number_pedido,
      ID_PRODUCTO: p.ID_PRODUCTO,
      PROVEEDOR_ID: proveedor.PROVEEDOR_ID,
      FINCA: proveedor.FINCA,
      N_SOLICITUD: p.N_SOLICITUD,
      PRODUCTO: p.PRODUCTO,
      STATUS: status,
    }));

  productosValidos.push(...filtrados);
}

// 2. Eliminar duplicados por combinación ID_PRODUCTO + N_SOLICITUD + numero de fila
const vistos = new Set();
const productosUnicos = productosValidos.filter(p => {
  const clave = `${p.N_SOLICITUD}-${p.row_number}`;
  if (vistos.has(clave)) return false;
  vistos.add(clave);
  return true;
});

// 3. Devolver los productos únicos
return productosUnicos.map(p => ({ json: p }));

