const existentes = $input.all(); // Datos del stock 
const nuevos = $items("items2"); // Lo que viene de "items"

const resultados = [];

for (const nuevo of nuevos) {
  const pedido = nuevo.json["NUM_PEDIDO"];
  const fecha = nuevo.json["FECHA"];
  const proveedor = nuevo.json["PROVEEDOR_ID"]
  const finca = nuevo.json["FINCA"];
  const productoId = nuevo.json["ID_PRODUCTO"];
  const prod = nuevo.json["PRODUCTO"];
  const palet = nuevo.json["PALET"];
  const cantPalets = parseInt(nuevo.json["CANT_PALETS"] || 0);
  const cajas = nuevo.json["CAJAS"];
  const cantCajas = parseInt(nuevo.json["CANT_CAJAS"] || 0);
  const entrega = nuevo.json["ENTREGA"]

  // Buscar si ya existe ese producto + palet en la hoja
  const existente = existentes.find(e =>
    e.json["PRODUCTO"] === prod &&
    e.json["PALET"] === palet &&
    e.json["NUM_PEDIDO"] === pedido &&
    e.json["PROVEEDOR_ID"] == proveedor

  );

  if (existente) {
    const totalPalets = parseInt(existente.json["CANT_PALETS"] || 0) + cantPalets;
    const totalCajas = parseInt(existente.json["CANT_CAJAS"] || 0) ;

    resultados.push({
      json: {
        mode: 'update',
        rowIndex: existente.json["row_number"],
        telefono: nuevo.json["TELEFONO"],
        data: {
          "NUM_PEDIDO": pedido,
          "PROVEEDOR_ID": proveedor,
          "PRODUCTO": prod,
          "PALET": palet,
          "CANT_PALETS": totalPalets,
          "CAJAS": cajas,
          "CANT_CAJAS": totalCajas
        }
      }
    });
  } else {
    resultados.push({
      json: {
        mode: 'append',
        data: {
          "NUM_PEDIDO": pedido,
          "FECHA": fecha,
          "PROVEEDOR_ID": proveedor,
          "FINCA": finca,
          "ID_PRODUCTO": productoId,
          "PRODUCTO": prod,
          "PALET": palet,
          "CANT_PALETS": cantPalets,
          "CAJAS": cajas,
          "CANT_CAJAS": cantCajas,
          "ENTREGA": entrega
        }
      }
    });
  }
}

return resultados;
