const BASE_URL = "https://crm.whitelabel.lat/api/v1/accounts/3/conversations";
const TOKEN = "uuWT9PNsY83ucax7HeqBEUSi";

async function getAllConversations() {
  let page = 1;
  const perPage = 25;
  const results = [];

  while (true) {
    const response = await this.helpers.httpRequest({
      method: "GET",
      url: `${BASE_URL}?status=all&page=${page}&per_page=${perPage}`,
      headers: {
        "api_access_token": TOKEN,
        "Content-Type": "application/json",
      },
      json: true,
    });

    const payload = response.data?.payload || [];

    if (payload.length === 0) break;

    for (const conv of payload) {
      results.push({
        conversation_id: conv.id,
        contact_id: conv.meta?.sender?.id,
        name: conv.meta?.sender?.name || null,
        phone_number: conv.meta?.sender?.phone_number || null,
        account_id: conv.account_id,
        labels: conv.labels || [],
        status: conv.status,
      });
    }

    page++;
  }

  return results.map(r => ({ json: r }));
}

return await getAllConversations();

const BASE_URL = "https://crm.whitelabel.lat/api/v1/accounts/3/conversations/1/messages?before=4218";
const TOKEN = "uuWT9PNsY83ucax7HeqBEUSi";

async function getMessages() {
  const response = await this.helpers.httpRequest({
    method: "GET",
    url: BASE_URL,
    headers: {
      "api_access_token": TOKEN,
      "Content-Type": "application/json",
    },
    json: true,
  });

  // El endpoint devuelve un array con "meta" y "payload"
  const payload = Array.isArray(response) ? (response[0]?.payload || []) : [];

  if (payload.length === 0) {
    return [{ json: { info: "No messages found" } }];
  }

  const results = payload.map(msg => ({
    id: msg.id,
    content: msg.content,
    content_type: msg.content_type,
  }));

  // n8n exige este formato
  return results.map(r => ({ json: r }));
}

return await getMessages();

