const data = items[0].json.data;

const uniqueCombos = new Set();
const result = [];

for (const entry of data) {
  const key = `${entry.PRODUCTO} | ${entry.PALET} | ${entry.CAJAS}`;

  if (!uniqueCombos.has(key)) {
    uniqueCombos.add(key);
    result.push({
      PRODUCTO: entry.PRODUCTO,
      PALET: entry.PALET,
      CAJAS: entry.CAJAS
    });
  }
}

return [{
  json: {
    data: result
  }
}];

