const mensaje = $json["quotedMessage"].conversation;

// Extraer bloques de pedidos
const pedidos = [];
const bloquesPedidos = mensaje.split(/‚û°Ô∏è \*?Pedido N¬∞: ([\w-]+)\*?/i);

let numeroPedidoGeneral = null;

for (let i = 1; i < bloquesPedidos.length; i += 2) {
    const numeroPedido = bloquesPedidos[i].trim();
    if (!numeroPedidoGeneral) {
        numeroPedidoGeneral = numeroPedido; // guardamos el primero
    }
    const contenido = bloquesPedidos[i + 1];

    // Extraer nombre del cliente
    const clienteMatch = contenido.match(/Cliente:\s*(.+)/i);
    const cliente = clienteMatch ? clienteMatch[1].trim() : null;

    // Extraer productos
    const productos = contenido
        .split(/\r?\n/)
        .filter(linea => linea.trim().startsWith('-'))
        .map(linea => {
            // Formato 1: - 20 palets (55 cajas) - PRODUCTO
            let itemMatch = linea.match(/- (\d+)\s*palets?\s*\((\d+)\s*cajas?\)\s*-\s*(.+)$/i);

            // Formato 2: - 20 X 55 - PRODUCTO
            if (!itemMatch) {
                itemMatch = linea.match(/- (\d+)\s*[xX]\s*(\d+)\s*-\s*(.+)$/i);
            }

            if (itemMatch) {
                return {
                    NUM_PEDIDO: numeroPedido,
                    NOMBRE_CLIENTE: cliente,
                    PRODUCTO: itemMatch[3].trim(),
                    CANT_PALETS: parseInt(itemMatch[1]),
                    CANT_CAJAS: parseInt(itemMatch[2])
                };
            }
            return null;
        })
        .filter(Boolean);

    pedidos.push(...productos);
}

return {
    NUM_PEDIDO: numeroPedidoGeneral,
    totalRegistros: pedidos.length,
    telefono: $json["N¬∫ DE TEL√âFONO"],
    FECHA: $json["FECHA"],  // usamos la que viene en input
    productos: pedidos
};

