const bruto = $json["output"];

// Limpieza básica igual que para el post
let soloTexto = bruto;
const delimitador = '__';
const idxDelim = soloTexto.indexOf(delimitador);
if (idxDelim !== -1) {
  soloTexto = soloTexto.substring(idxDelim + delimitador.length).trim();
}
soloTexto = soloTexto.replace(/^\s+/, '');

// Genera título automático tomando la primera frase/línea del texto, si no tienes otro campo
const titulo = soloTexto.split('\n')[0].trim() || "Blog generado automáticamente";

// Si tu tabla NocoDB para blog tiene campo de tags o etiquetas, puedes generarlas:
const palabrasClave = soloTexto
  .replace(/[.,;:\n]/g, ' ')
  .split(' ')
  .filter(w => w.length > 5)        // Palabras con más de 5 letras
  .slice(0,5);                      // Selecciona las primeras 5
const tags = palabrasClave.join(', ');

// Resumen automático: recorta a 350 caracteres, terminando en la última palabra completa
let resumen = soloTexto.substring(0, 350);
const ultimoEspacio = resumen.lastIndexOf(' ');
if (ultimoEspacio > 0) resumen = resumen.substring(0, ultimoEspacio) + '...';

// Guarda el registro para NocoDB (ajusta los campos a tu tabla real)
return {
  Title: titulo,
  'Blog Post': soloTexto,      // Texto completo
  Fecha: '24-11-2025',         // O usa fecha dinámica si prefieres
  Hora: '15:47',
  Status: 'Published',         // O Pending si va por revisión
  Tags: tags,
  Summary: resumen
};
